
public class SwitchClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int day = 2;
		
		switch (day) {
		case 1: 
			System.out.println("monday");
			
		case 2:
			System.out.println("tueday");

		case 3 :
			System.out.println("wedseday");
			break;
		case 4: 
			System.out.println("thurday");
			

		}
	}

}
